<div class="layui-card layui-bg-gray"><?php if(!(empty($title) || (($title instanceof \think\Collection || $title instanceof \think\Paginator ) && $title->isEmpty()))): ?><div class="layui-card-header notselect"><span class="layui-icon layui-icon-next font-s10 color-desc margin-right-5"></span><?php echo htmlentities((isset($title) && ($title !== '')?$title:'')); ?><div class="pull-right"><div class="color-red">过滤手机第一次运行脚本时,会自动进行注册添加</div></div></div><?php endif; ?><div class="layui-card-body"><div class="think-box-shadow table-block"><table class="layui-table margin-top-10" lay-skin="line"><?php if(!(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty()))): ?><thead><tr><th class='list-table-check-td think-checkbox'><label><input data-auto-none data-check-target='.list-check-box' type='checkbox'></label></th><th class='text-left nowrap'>手机设备号</th><th class='text-left nowrap'>状态</th><th class='text-left nowrap'>授权状态</th><th></th></tr></thead><tbody><?php foreach($list as $key=>$vo): ?><tr><td class='list-table-check-td think-checkbox'><label><input class="list-check-box" value='<?php echo htmlentities($vo['id']); ?>' type='checkbox'></label></td><td class="text-left nowrap"><span class="font-w7"><?php echo htmlentities((isset($vo['deviceid']) && ($vo['deviceid'] !== '')?$vo['deviceid']:'-')); ?></span></td><td class='text-left nowrap'><!--<?php if($vo['states'] == 0): ?>--><p class="color-red">禁用</p><!--<?php elseif($vo['states'] == 1): ?>--><p class="color-green">启用</p><!--<?php endif; ?>--></td><td class='text-left nowrap'><!--<?php if($vo['authorization'] == 0): ?>--><p class="color-red">未授权</p><!--<?php elseif($vo['authorization'] == 1): ?>--><p class="color-green">已授权</p><!--<?php endif; ?>--></td><td class='text-left nowrap'><!--<?php if(auth("enable") and $vo['states'] == 0 and $vo['authorization'] == 1): ?>--><a class="layui-btn layui-btn-xs" data-action="<?php echo url('enable'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>" data-csrf="<?php echo systoken('enable'); ?>">启 用</a><!--<?php elseif(auth("enable") and $vo['states'] == 1 and $vo['authorization'] == 1): ?>--><a class="layui-btn layui-btn-xs layui-btn-warm" data-action="<?php echo url('enable'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>" data-csrf="<?php echo systoken('enable'); ?>">禁 用</a><!--<?php endif; ?>--><!--<?php if(auth("authorization") and $vo['authorization'] == 0): ?>--><a class="layui-btn layui-btn-xs" data-action="<?php echo url('grant'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>" data-csrf="<?php echo systoken('grant'); ?>">授 权</a><!--<?php elseif(auth("authorization") and $vo['authorization'] == 1): ?>--><a class="layui-btn layui-btn-xs layui-btn-warm" data-action="<?php echo url('grant'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>" data-csrf="<?php echo systoken('grant'); ?>">取消授权</a><!--<?php endif; ?>--><!--<?php if(auth("remove")): ?>--><a class="layui-btn layui-btn-xs layui-btn-danger" data-confirm="确定要删除过滤手机吗？<br/>请确保手机上已经删除脚本" data-action="<?php echo url('remove'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>" data-csrf="<?php echo systoken('remove'); ?>">删 除</a><!--<?php endif; ?>--></td></tr><?php endforeach; ?></tbody><?php endif; ?></table><?php if(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty())): ?><span class="notdata">您还没有任何过滤手机</span><?php else: ?><?php echo (isset($pagehtml) && ($pagehtml !== '')?$pagehtml:''); ?><?php endif; ?></div></div><script>
    (function (layers) {
        $('body').off('click', '[data-tips-desc]').on('click', '[data-tips-desc]', function () {
            return layers.push(layer.tips($(this).find('textarea').html(), this, {tips: 3, time: 0, maxWidth: 500})), false;
        }).off('click', '.layui-body').on('click', '.layui-body', function () {
            layers.forEach(function (value, index) {
                layer.close(value), delete layers[index];
            });
        });
        $(window).on('hashchange', function () {
            $('.layui-body').trigger('click');
        });
    })([]);
</script></div>