<div class="layui-card layui-bg-gray"><?php if(!(empty($title) || (($title instanceof \think\Collection || $title instanceof \think\Paginator ) && $title->isEmpty()))): ?><div class="layui-card-header notselect"><span class="layui-icon layui-icon-next font-s10 color-desc margin-right-5"></span><?php echo htmlentities((isset($title) && ($title !== '')?$title:'')); ?><div class="pull-right"><!--<?php if(auth("remove")): ?>--><button data-action='<?php echo url("remove"); ?>' data-csrf="<?php echo systoken('remove'); ?>" data-rule="id#{key}" class='layui-btn layui-btn-sm layui-btn-danger'>删除短信</button><!--<?php endif; ?>--><!--<?php if(auth("remove")): ?>--><button data-modal='<?php echo url("clearall"); ?>' data-csrf="<?php echo systoken('clearall'); ?>" class='layui-btn layui-btn-sm layui-btn-danger'>清空全部短信</button><!--<?php endif; ?>--></div></div><?php endif; ?><div class="layui-card-body"><div class="think-box-shadow table-block"><table class="layui-table margin-top-10" lay-skin="line"><?php if(!(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty()))): ?><thead><tr><th class='list-table-check-td think-checkbox'><label><input data-auto-none data-check-target='.list-check-box' type='checkbox'></label></th><th class='text-left nowrap'>发送号码</th><th class='text-left nowrap'>接收号码</th><th class='text-left nowrap'>短信内容</th><th class='text-left nowrap'>接收时间</th><th></th></tr></thead><tbody><?php foreach($list as $key=>$vo): ?><tr><td class='list-table-check-td think-checkbox'><label><input class="list-check-box" value='<?php echo htmlentities($vo['id']); ?>' type='checkbox'></label></td><td class="text-left nowrap"><span class="font-w7"><?php echo htmlentities((isset($vo['send_number']) && ($vo['send_number'] !== '')?$vo['send_number']:'-')); ?></span></td><td class="text-left nowrap"><span class="font-w7"><?php echo htmlentities((isset($vo['reveice_number']) && ($vo['reveice_number'] !== '')?$vo['reveice_number']:'-')); ?></span></td><td class="text-left nowrap"><span class="font-w7"><?php echo htmlentities((isset($vo['reveice_message']) && ($vo['reveice_message'] !== '')?$vo['reveice_message']:'-')); ?></span></td><td class="text-left nowrap"><span class="font-w7"><?php echo htmlentities((isset($vo['reveice_time']) && ($vo['reveice_time'] !== '')?$vo['reveice_time']:'-')); ?></span></td><td class='text-left nowrap'><!--<?php if(auth("remove")): ?>--><a class="layui-btn layui-btn-xs layui-btn-danger" data-confirm="确定要删除短信吗？" data-action="<?php echo url('remove'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>" data-csrf="<?php echo systoken('remove'); ?>">删 除</a><!--<?php endif; ?>--></td></tr><?php endforeach; ?></tbody><?php endif; ?></table><?php if(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty())): ?><span class="notdata color-red">还 没 有 收 到 任 何 短 信!</span><?php else: ?><?php echo (isset($pagehtml) && ($pagehtml !== '')?$pagehtml:''); ?><?php endif; ?></div></div><script>
    (function (layers) {
        $('body').off('click', '[data-tips-desc]').on('click', '[data-tips-desc]', function () {
            return layers.push(layer.tips($(this).find('textarea').html(), this, {tips: 3, time: 0, maxWidth: 500})), false;
        }).off('click', '.layui-body').on('click', '.layui-body', function () {
            layers.forEach(function (value, index) {
                layer.close(value), delete layers[index];
            });
        });
        $(window).on('hashchange', function () {
            $('.layui-body').trigger('click');
        });
    })([]);
</script></div>